INSERT INTO `page` VALUES ('51', '学院简介', '', '', '&lt;div align=&quot;left&quot;&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;img src=&quot;/Public/kindeditor/attached/image/20150521/20150521035355_55925.png&quot; alt=&quot;&quot; height=&quot;135&quot; width=&quot;739&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot;&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;哈尔滨工业大学材料学科是国家一级重点学科，在国内外具有重要影响。哈工大（威海）材料科学与工程学院成立于2004年，现有“材料成型与控制工程”、\r\n“焊接技术与工程”、“材料科学与工程”和“电子封装技术”四个本科专业，设有“材料加工工程”和“材料学”两个硕士点，与总校共用“材料科学与工程”一\r\n级学科博士点和博士后流动站。目前在校本科生约1000人、研究生40余人。学院拥有“材料加工工程”山东省重点学科、 \r\n“焊接技术与工程”山东省特色专业、“特种焊接技术”山东省重点实验室、“焊接技术与表面工程”山东省工程技术中心、“生物与医用材料”哈工大优秀科技创\r\n新团队。&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;目前学院有学术人员50人，其中院士3人、教授19人（其中博士生导师12人）、副教授8人，有6人在国家相应学术组织中任副理事长以上职务，形成了职称结构、年龄结构和学缘结构比较合理的师资结构。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;科学研究方面，威海校区材料学院本着突出特色、强化优势、为山东省和威海市地方经济建设服务的原则，重点在（1）以特种功能材料和电子封装技术为主的高新\r\n技术领域；（2）以材料成型与焊接技术为主的先进制造技术领域；（3）以水下焊接、腐蚀防护、海水淡化系统、海洋资源材料、船舶工程材料、水下清洁能源材\r\n料为主的海洋工程领域开展科研工作。现已形成“材料成型与仿真技术”、“特种焊接与表面工程”、“电子封装技术与材料”、“特种陶瓷与纳米材料”、“复合\r\n材料与结构”、“粉末冶金与氢能材料”、“金属材料与材料模拟”等研究方向。研究人员承担了多项国家自然学科基金、国家863、973、国防预研计划基金\r\n以及企业横向课题等科研项目。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n学院设有实验教学中心（包括材料成型实验室、焊接实验室、电子封装实验室、金相实验室、材料性能实验室、材料化学实验室）、特种焊接实验室、先进材料研究\r\n所、复合材料与结构研究所等实验教学和科研机构。学校及合作企业先后投入1500多万元用于实验教学和科研硬件条件建设，购买了一批先进仪器设备，为教学\r\n和科研提供了有力保障。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;本学科注重学术交流与国际化办学，与伦敦大学、伯明翰大学、曼彻斯特大学建立了稳定的科学研究和人才培养方面的国际合作，为学院学生提供了出国留学的机会。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;学院注重学风建设，注重学生创新能力培养和综合素质教育，以“真材实料、锻焊大器”的目标培养学生，近三年的学生考研率平均达40%，就业率达98%以上。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 我们将充分利用哈工大人才和学科优势以及威海市环境和经济优势，努力把威海校区材料学院建成一个高水平人才培养和科学研究基地。&lt;/span&gt;&lt;/span&gt; \r\n	&lt;/p&gt;\r\n&lt;/div&gt;', '1432174068');
INSERT INTO `page` VALUES ('61', '重点实验室', '', '', '&lt;span style=&quot;font-family:SimSun;font-size:24px;&quot;&gt;&lt;em&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/em&gt;&lt;/span&gt;&lt;span style=&quot;font-family:SimSun;font-size:24px;&quot;&gt;&lt;em&gt;&lt;strong&gt;山东省重点实验室&lt;/strong&gt;&lt;/em&gt;&lt;/span&gt; \r\n&lt;p align=&quot;left&quot;&gt;\r\n	&lt;span style=&quot;font-family:SimSun;font-size:16px;&quot;&gt;&lt;em&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/em&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;hr /&gt;\r\n&lt;div align=&quot;left&quot;&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;color:#337FE5;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:18px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:18px;font-family:Microsoft YaHei;&quot;&gt;&lt;span style=&quot;font-size:18px;font-family:Microsoft YaHei;&quot;&gt;特种焊接实验室&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;color:#337FE5;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;span style=&quot;font-size:8pt;color:black;font-family:宋体;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;特种焊接实验室以哈尔滨工业大学“材料科学与工程&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;”&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;国\r\n家一级重点学科为支撑，联合山东省科学院海洋仪器仪表研究所、乌克兰巴顿焊接研究所等，以山东核电设备制造有限公司、威海船厂等特种焊接技术终端用户作为\r\n技术研发试验基地。未来三年内，实验室将结合山东传统工业技术革新需求和新兴涉海产业装备技术需求，开展水下焊接及切割技术、微连接、特种焊接材料、高效\r\n焊接技术及装备的研究，不但可以为新兴海洋工业、核电工业、船舶工业提供技术支撑和智力支持，而且为山东传统优势冶金、电子、机械等工业的技术革新和产品\r\n升级提供理论基础和工艺指导。&lt;/span&gt;&lt;/span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;实验室目前拥有一支高水平、专门从事特种焊接技术研究开发的队伍，在山东省相关领域具有明显技术优势，已获国家973、&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;863&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;、国家自然科学基金等省部级资助项目15项，在研企事业横向合作课题&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;项。实验室主任冯吉才教授兼任教育部科技委材料学部委员、中国焊接学会副理事长，是我国现代焊接生产技术领域知名专家。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n	&lt;/p&gt;\r\n&lt;/div&gt;', '1432177920');
INSERT INTO `page` VALUES ('62', '分析测试中心', '', '', '&lt;p align=&quot;center&quot;&gt;\r\n	&lt;span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong&gt;分析测试中心&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p align=&quot;center&quot;&gt;\r\n	&lt;span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;哈尔滨工业大学（威海）分析测试中心是学校组建的面向全校的公共测试服务平台，挂靠\r\n研究生处管理，依托材料科学与工程学院进行日常使用和维护。目前，分析测试中心拥有核磁共振仪、透射电子显微镜、扫描电子显微镜、能谱仪、X射线衍射仪、\r\nX射线荧光光谱仪、傅里叶红外光谱仪等先进的分析测试仪器，实验教学用地面积近500m&lt;/span&gt;&lt;sup&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;2&lt;/span&gt;&lt;/sup&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;。&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; \r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;分析测试中心以全心全意为学校的教学、科研提供一流服务为宗旨，以提高学校科学研究、学科建设和人才培养水平为目标，为全校师生开展科学研究提供重要的技\r\n术支撑。同时，充分发挥大型仪器资源共享的作用，分析测试中心的分析测试平台本着服务到家、规格严格的工作方针，遵循“资源共享，合理利用”的原则，在确\r\n保为我校教学、科研提供优质服务的前提下，面向社会开展广泛的学术交流与合作，为企事业单位提供技术咨询、产品鉴定、失效分析和委托研究等服务，以科学、\r\n可靠、准确的检测手段，为地方经济建设和科学技术水平的提高贡献力量。 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;办公地点：石墨楼214室 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;网&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 址： &lt;/span&gt;&lt;a href=&quot;http://atc.hitwh.edu.cn/&quot; target=&quot;_blank&quot;&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;http://atc.hitwh.edu.cn/ &lt;/span&gt;&lt;/a&gt;&lt;/span&gt; \r\n&lt;/p&gt;', '1432177994');
INSERT INTO `page` VALUES ('63', '科研概况', '', '', '&lt;div align=&quot;center&quot;&gt;\r\n	&lt;p&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:32px;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;科研概况&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt; \r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt; &lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21pt;&quot; align=&quot;left&quot;&gt;\r\n		&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;哈工大威海校区材料学科以总校材料学科为后盾，发展与总校互补的特色研究方向。选择以电子信息、生物医学、新能源和国防军工为背景的特种功能材料及加工技术作为主要研究领域。威海校区研究人员目前承担着国家自然科学基金、国家&lt;/span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;863&lt;/span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;、&lt;/span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;973&lt;/span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;、国防预研计划基金以及企业横向课题等多项科研项目。与总校共同建立的“哈尔滨工业大学（威海）新材料与成形技术研究中心”在承接国家重大项目上发挥了重要作用。目前承担多个国防重点工程科研任务，在威海校区的经费达&lt;/span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;500&lt;/span&gt;&lt;span style=&quot;font-family:Microsoft YaHei;font-size:16px;&quot;&gt;多\r\n万元。一批新的重点项目也将落户在威海校区。这些项目的开展将在我国导弹、鱼雷、卫星、空间环境模拟等领域发挥重要作用，也将推动威海校区特色研究方向的\r\n形成。威海校区材料学院注重为地方区域经济发展服务，加强与威海市和山东半岛的新材料相关企业的联系，与地方政府和企业一起共同打造具有地方特色的强势经\r\n济和强势学科。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n&lt;/div&gt;', '1432178522');
INSERT INTO `page` VALUES ('64', '科研方向', '', '', '&lt;div align=&quot;center&quot;&gt;\r\n	&lt;p&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;科研方向&lt;/span&gt;&lt;/strong&gt; \r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;left&quot;&gt;\r\n		&lt;span style=&quot;font-size:10.5pt;font-family:宋体;&quot;&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt; &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot;&gt;\r\n		&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;&lt;span&gt;&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;弥补山东省材料学科空缺：电子封装、轧制、焊接、复合材料&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;开辟哈工大材料学科新方向：轧钢、热冲压、水下焊接、船舶与海洋工程复合材料&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;船舶与汽车制造加工技术（成型、焊接）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;海洋工程材料与技术（焊接、材料）&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;信息与新能源材料与技术（电子封装、材料）&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;MsoNormal&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n&lt;/div&gt;', '1432178532');
INSERT INTO `page` VALUES ('65', '科研团队', '', '', '&lt;div align=&quot;center&quot;&gt;\r\n	&lt;p&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;科研团队&lt;/span&gt;&lt;/strong&gt; \r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;br /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;table style=&quot;width:80%;&quot; class=&quot;MsoNormalTable&quot; border=&quot;1&quot; bordercolor=&quot;#CCCCCC&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;\r\n			&lt;tbody&gt;\r\n				&lt;tr&gt;\r\n					&lt;td style=&quot;background:#8db3e2;border:black 1pt solid;&quot; width=&quot;156&quot;&gt;\r\n						&lt;p class=&quot;MsoNormal&quot;&gt;\r\n							&lt;b&gt;&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;科研团队&lt;span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/b&gt; \r\n						&lt;/p&gt;\r\n					&lt;/td&gt;\r\n					&lt;td style=&quot;background:#8db3e2;border:black 1pt solid;&quot; width=&quot;92&quot;&gt;\r\n						&lt;p class=&quot;MsoNormal&quot;&gt;\r\n							&lt;b&gt;&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;带头人&lt;span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/b&gt; \r\n						&lt;/p&gt;\r\n					&lt;/td&gt;\r\n					&lt;td style=&quot;background:#8db3e2;border:black 1pt solid;&quot; width=&quot;332&quot;&gt;\r\n						&lt;p class=&quot;MsoNormal&quot;&gt;\r\n							&lt;b&gt;&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;主要研究内容与应用领域&lt;span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/b&gt; \r\n						&lt;/p&gt;\r\n					&lt;/td&gt;\r\n				&lt;/tr&gt;\r\n				&lt;tr&gt;\r\n					&lt;td style=&quot;background:#b6dde8;border:black 1pt solid;&quot; width=&quot;156&quot;&gt;\r\n						&lt;p class=&quot;MsoNormal&quot;&gt;\r\n							&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;复合材料与结构&lt;span&gt;&lt;/span&gt;&lt;/span&gt; \r\n						&lt;/p&gt;\r\n						&lt;p class=&quot;MsoNormal&quot;&gt;\r\n							&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;（跨院系）&lt;span&gt;&lt;/span&gt;&lt;/span&gt; \r\n						&lt;/p&gt;\r\n					&lt;/td&gt;\r\n					&lt;td style=&quot;background:#b6dde8;border:black 1pt solid;&quot; valign=&quot;top&quot; width=&quot;92&quot;&gt;\r\n						&lt;p class=&quot;MsoNormal&quot; style=&quot;vertical-align:top;&quot;&gt;\r\n							&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;杜善义 院士&lt;span&gt;&lt;/span&gt;&lt;/span&gt; \r\n						&lt;/p&gt;\r\n						&lt;p class=&quot;MsoNormal&quot; style=&quot;vertical-align:top;&quot;&gt;\r\n							&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;边文凤 教授&lt;span&gt;&lt;/span&gt;&lt;/span&gt; \r\n						&lt;/p&gt;\r\n						&lt;p class=&quot;MsoNormal&quot; style=&quot;vertical-align:top;&quot;&gt;\r\n							&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;黄龙男 教授&lt;span&gt;&lt;/span&gt;&lt;/span&gt; \r\n						&lt;/p&gt;\r\n						&lt;p class=&quot;MsoNormal&quot; style=&quot;vertical-align:top;&quot;&gt;\r\n							&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;温广武 教授&lt;span&gt;&lt;/span&gt;&lt;/span&gt; \r\n						&lt;/p&gt;\r\n					&lt;/td&gt;\r\n					&lt;td style=&quot;background:#b6dde8;border:black 1pt solid;&quot; width=&quot;332&quot;&gt;\r\n						&lt;p class=&quot;MsoNormal&quot;&gt;\r\n							&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;*&lt;/span&gt;&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;碳纤维与复合材料&lt;span&gt;&lt;/span&gt;&lt;/span&gt; \r\n						&lt;/p&gt;\r\n						&lt;p class=&quot;MsoNormal&quot;&gt;\r\n							&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;*&lt;/span&gt;&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;复合材料力学与结构设计&lt;span&gt;&lt;/span&gt;&lt;/span&gt; \r\n						&lt;/p&gt;\r\n						&lt;p class=&quot;MsoNormal&quot;&gt;\r\n							&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;*&lt;/span&gt;&lt;span style=&quot;font-size:9pt;font-family:宋体;&quot;&gt;在汽车、船舶、海洋平台、潜水器上的应用&lt;span&gt;&lt;/span&gt;&lt;/span&gt; \r\n						&lt;/p&gt;\r\n					&lt;/td&gt;\r\n				&lt;/tr&gt;\r\n				&lt;tr&gt;\r\n					&lt;td style=&quot