INSERT INTO `news` VALUES ('249', '122', '0', '关于《学生工作简报》征稿的通知', 'hzy', '1431596368', '0', '', '1431596368', '3', '0', '', 0xE8BF99E4B8AAE698AFE696B0E997BBE58685E5AEB9E79A84E7AE80E8A681E6A682E8BFB0, '&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;关于《学生工作简报》征稿的通知&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;各位领导老师：&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;我校《学生工作简报》即将开办，将成为我校学生工作动态的汇集地和发布地，既是各院系学生工作的交流平台，也是各级领导了解学生工作的有效窗口。希望得到各院系领导老师的大力支持，现将稿件征稿事宜发布如下：&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 42.75pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;一、&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;栏目设置&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 21.75pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;学风建设，班级建设，寝室建设，工作交流，学子风采，院系短讯，案例分析&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 42.75pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;二、&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;报送要求&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 39.75pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;1.&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;每月12日前和28日前报送；&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 39.75pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;2.&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;稿件需注明所属栏目；&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 39.75pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;3.&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;稿件只接收电子版，&lt;a href=&quot;mailto:%E5%8F%91%E9%80%81%E8%87%B3wangrui1425@sin.com&quot;&gt;&lt;span&gt;&lt;span&gt;&lt;span&gt;发送至wangrui1425@sin.com&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;；&lt;/a&gt;&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 39.75pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;4.&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;所报内容要有真实性和实时性；&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 39.75pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;5.&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;所报稿件以新闻的格式进行编写；&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 21.75pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;每学期末将对各院系的稿件刊发情况进行总结和评比。希望各院系充分利用和发挥本院记者团的力量，积极报送优质稿件.&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 21.75pt;&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 21.75pt;&quot; align=&quot;right&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;学生工作处 教育管理办公室&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 21.75pt;&quot; align=&quot;right&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 21.75pt;&quot; align=&quot;right&quot;&gt;\r\n	&lt;span style=&quot;font-size:12pt;line-height:150%;&quot;&gt;2010&lt;span&gt;&lt;span&gt;年11&lt;/span&gt;&lt;span&gt;月4&lt;/span&gt;&lt;span&gt;日&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/div&gt;', '', '0', '1', '', '0', '18');
INSERT INTO `news` VALUES ('250', '122', '0', '关于限时提供WLAN无线网络信号的通知', 'hzy', '1431596503', '0', '', '1431596503', '8', '7', '', 0xE8BF99E4B8AAE698AFE696B0E997BBE58685E5AEB9E79A84E7AE80E8A681E6A682E8BFB0, '&lt;h3&gt;\r\n	&lt;span&gt;&lt;strong&gt; 关于限时提供WLAN无线网络信号的通知&lt;/strong&gt;&lt;/span&gt;\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;text-align:center;&quot; align=&quot;center&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:28pt;&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:28pt;&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;部分学生在晚上熄灯后有上网的习惯，不能够保证充足的睡眠，影响第二天上课的质量，严重的还影响其他同寝室同学的休息。鉴于此，经过认真调查研究，并广泛听取学生的意见，学校决定，自&lt;/span&gt;&lt;span style=&quot;font-size:14pt;&quot;&gt;2012&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size:14pt;&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;月&lt;/span&gt;&lt;span style=&quot;font-size:14pt;&quot;&gt;21&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;日始在学生公寓限时提供&lt;/span&gt;&lt;span style=&quot;font-size:14pt;&quot;&gt;WLAN&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;无线网络信号。网络信号屏蔽时间：&lt;b&gt;星期日——星期四晚上&lt;/b&gt;&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-size:14pt;&quot;&gt;23:30&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;——次日&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-size:14pt;&quot;&gt;6:00&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;；寒暑假、法定节假日网络正常（放假前一天晚上正常使用，放假最后一天晚上&lt;/span&gt;&lt;span style=&quot;font-size:14pt;&quot;&gt;23:30&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;起屏蔽）。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:28pt;&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;另外，部分同学在&lt;/span&gt;&lt;span style=&quot;font-size:14pt;&quot;&gt;BBS&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;对\r\n无线网络的问题进行了广泛的讨论，提出了一些建设性的意见和建议，学生处对同学们的意见和建议非常重视，并据此对方案进行了适当的调整。但也有极少数同学\r\n在网上借题发挥，发表不负责任的言论，甚至有煽动性语言，对此，相关部门正在进行调查了解，一旦查实将给予严肃的处理。同时，号召广大同学要从加强学校学\r\n风建设和保证学生身体健康的高度深入理解这项工作的精神，支持此项工作的开展。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:28pt;&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:-182pt;margin-left:213.9pt;&quot;&gt;\r\n	&lt;span style=&quot;font-size:14pt;&quot;&gt;&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;学生工作处&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:-182pt;margin-left:213.9pt;&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;font-size:14pt;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:&amp;quot;font-size:14pt;&quot;&gt;&lt;span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;2012&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;span&gt;年&lt;/span&gt;&lt;/span&gt;5&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;span&gt;月&lt;/span&gt;&lt;/span&gt;21&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;span&gt;日&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;', '', '0', '0', '', '0', '18');
INSERT INTO `news` VALUES ('251', '122', '0', '转发《关于2010年度研究生德育研究项目申报工作的通知》', 'hzy', '1431596563', '0', '', '1431596563', '7', '0', '', 0xE8BF99E4B8AAE698AFE696B0E997BBE58685E5AEB9E79A84E7AE80E8A681E6A682E8BFB0, '&lt;div align=&quot;center&quot;&gt;\r\n	&lt;span style=&quot;font-size:15pt;&quot;&gt;转发《关于2010年度研究生德育研究项目申报工作的通知》&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;为进一步探索新时期研究生德育工作的新规律、新内涵、新途径与新方法，梳理各单位研究生德育工作的传统和特色，促进理论与实践的有机结合，提升研究生德育工作的水平，现在全校范围内开展研究生德育研究项目申报工作，有关事项通知如下：&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;一、项目类别及资助额度&lt;/span&gt;&lt;/b&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;项目分为：&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;1.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;重点项目，资助经费不超过3000元；&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;2.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;基础项目，资助经费不超过2000元；&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;3.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;一般项目，资助经费不低于1000元。&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;二、项目内容&lt;/span&gt;&lt;/b&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;1.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生党支部建设研究&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;2.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;博士生社会实践路径研究&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;3.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生校院系管理体系研究&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;4.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生思想状况调查&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;5.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生心理健康教育体系研究&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;6.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生团队精神教育体系构建&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;7.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生责任意识培养&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;8.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生人际交往路径研究&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;9.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;导师评价机制研究&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;10.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生德育管理信息系统建设研究&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;11.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生国际交往能力建设&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;12.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生安全稳定长效工作机制的研究&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;13.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;其他有利于研究生发展的自拟项目&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;三、申报条件&lt;/span&gt;&lt;/b&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;1.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;申请者实际从事研究生教育工作。&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;2.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;申请者能承担和负责组织项目的实施；每个申请者限报一个项目；所列课题组成员须征得成员本人同意；上期立项未结题者限报。&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;margin:0cm 0cm 0pt 3pt;&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;3.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;申请者应如实填报材料，确保无争议。&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;四、相关要求&lt;/span&gt;&lt;/b&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;1.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;申请者需填写《2010年度研究生德育研究项目申请表》（见附件）并于11月10日前将电子版发送至研工部，纸质版由院（系）签字后送至行政楼317。自立项公布之日起，按照课题申报书内的时间进度开展研究。研究时间一般为1-2年。&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;2.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;中期检查。提交课题进展报告，内容应包括课题进展情况，发表论文等情况，以及课题研究计划遇到的问题和解决的方法等。字数不限。&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;3.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;课题结题。提交课题结题报告，内容包括课题研究成果，发表论文等情况。字数在5000字以上。根据课题的完成情况进行验收、经费报销。&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;4.&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;本次立项优秀项目推荐到校党建与思想政治工作研究会和校外相关研究机构进行立项。&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;联 系 人：李岳&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;联系电话：86416419&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;电子信箱：liyue@hit.edu.cn&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;附件：&lt;a href=&quot;http://today.hit.edu.cn/uploadfiles/2010/9-27/%E9%99%84%E4%BB%B62010%E5%B9%B4%E5%BA%A6%E7%A0%94%E7%A9%B6%E7%94%9F%E5%BE%B7%E8%82%B2%E7%A0%94%E7%A9%B6%E9%A1%B9%E7%9B%AE%E7%94%B3%E8%AF%B7%E8%A1%A8.doc&quot;&gt;&lt;span style=&quot;color:windowtext;text-decoration:none;&quot;&gt; 2010&lt;span&gt;年度研究生德育研究项目申请表&lt;/span&gt;&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:right;&quot; align=&quot;right&quot;&gt;\r\n	&amp;nbsp;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;研究生工作部&amp;nbsp;&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:right;&quot; align=&quot;right&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;2010&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-size:12pt;&quot;&gt;年11月1日&lt;/span&gt;\r\n&lt;/div&gt;', '', '0', '0', '', '0', '18');

